diagram:
graph {
  graph [bgcolor=transparent]
  node [shape=box, style=rounded, fontname="NanumGothic", fontsize=12]
  edge [fontname="NanumGothic", fontsize=12]

  subgraph cluster_applications {
    label=<<FONT FACE="NanumGothic">YOLO의 적용 분야</FONT>>;
    color=blue;

    node1 [label=<<FONT FACE="NanumGothic">자율주행</FONT>>];
    node2 [label=<<FONT FACE="NanumGothic">보조기기</FONT>>];
    node3 [label=<<FONT FACE="NanumGothic">로봇 시스템</FONT>>];

    edge [style=dashed];
    node1 -- node2;
    node2 -- node3;
  }

  subgraph cluster_benefits {
    label=<<FONT FACE="NanumGothic">YOLO의 장점</FONT>>;
    color=green;

    node4 [label=<<FONT FACE="NanumGothic">실시간 객체 감지</FONT>>];
    node5 [label=<<FONT FACE="NanumGothic">상황 파악 능력</FONT>>];
    node6 [label=<<FONT FACE="NanumGothic">지능형 시스템의 핵심 기술</FONT>>];

    edge [style=solid];
    node4 -- node5;
    node5 -- node6;
  }

  edge [style=dashed, color=grey];
  node1 -- node4;
  node2 -- node5;
  node3 -- node6;
}

